import { Directive, input } from '@angular/core';
import { RouterLink } from '@angular/router';

@Directive({
  selector: 'ngx-govuk-breadcrumb',
})
export class GovUKBreadcrumbDirective {
  readonly label = input.required<string>();
  readonly link = input<RouterLink['routerLink']>();
}

import { Directive, input } from '@angular/core';

export const inputCommonInputs = ['autocomplete', 'extraClasses', 'inputId'];

@Directive({
  })
export class GovUKCommonFormInputDirective {
  readonly autocomplete = input<AutoFill>();
  readonly extraClasses = input<string>();
  readonly inputId = input<string>();
}

import { Directive, input } from '@angular/core';

/**
 * Directive for an individual error summary item
 *
 * This directive is used declaratively and rendered by the parent error summary component.
 * It doesn't render anything itself - it just provides a declarative API for the error summary.
 *
 * The component uses Angular Router's fragment navigation to enable scrolling to the target element.
 * For proper scrolling behavior, you must enable anchor scrolling in your application's router
 * configuration using either:
 *
 * - RouterModule.forRoot(routes, { anchorScrolling: 'enabled' })
 * - withInMemoryScrolling({ anchorScrolling: 'enabled' })
 */
@Directive({
  selector: 'ngx-govuk-error-summary-item',
  })
export class GovUKErrorSummaryItemDirective {
  /**
   * The text of the error message to display
   * Required field that will be displayed as the error message
   */
  readonly text = input.required<string>();

  /**
   * Required targetId attribute for the error link
   * This should reference the id of the form field with the error.
   * The component will use Angular Router's fragment navigation to create a link to this element.
   *
   * Note: For proper scrolling behavior, anchor scrolling must be enabled in your router configuration.
   */
  readonly targetId = input.required<string>();
}

import { booleanAttribute, Directive, input } from '@angular/core';

@Directive({
  selector: 'ngx-govuk-radio-option',
  })
export class GovUKRadioOptionDirective {
  readonly label = input.required<string>();
  readonly value = input.required<string>();

  readonly hint = input<string>();
  readonly isDivided = input(false, { transform: booleanAttribute });
}

import { Directive, input } from '@angular/core';
import { RouterLink } from '@angular/router';

@Directive({
  selector: 'ngx-govuk-service-navigaiton-item',
  })
export class GovUKServiceNavigationItemDirective {
  readonly label = input.required<string>();
  readonly link = input<RouterLink['routerLink']>();
}

import { Directive, TemplateRef } from '@angular/core';

/**
 * Directive for the value content of a summary list item
 *
 * This directive allows you to define the value content as a template
 * which can include complex HTML.
 *
 * @example
 * ```html
 * <ng-template ngxGovUkSummaryListValue>
 *   <p>72 Guild Street<br>London<br>SE23 6FH</p>
 * </ng-template>
 * ```
 */
@Directive({
  selector: '[ngxGovUkSummaryListValue]',
  })
export class GovUKSummaryListValueDirective {
  constructor(public templateRef: TemplateRef<unknown>) {}
}

import { booleanAttribute, Directive, input } from '@angular/core';
import { GovUKTagColor } from 'ngx-govuk-frontend/tag';

/**
 * Directive for an individual task list item
 *
 * This directive is used declaratively and rendered by the parent task list component.
 * It doesn't render anything itself - it just provides a declarative API for the task list.
 */
@Directive({
  selector: 'ngx-govuk-task-list-item',
})
export class GovUKTaskListItemDirective {
  /**
   * The title of the task to display
   * Required field that will be displayed as the main text for the task
   */
  readonly title = input.required<string>();

  /**
   * Text to display as the status of the task
   * This will be shown either as plain text or within a tag depending on whether tagColor is provided
   */
  readonly statusText = input.required<string>();

  /**
   * Optional hint text to display below the task title
   * Provides additional context or guidance for the task
   */
  readonly hint = input<string>();

  /**
   * Optional URL for the task link
   * If provided and noLink is false, the task title will be rendered as a link to this URL
   */
  readonly href = input<string>();

  /**
   * Optional Angular router link for the task
   * If provided and noLink is false, the task title will be rendered as a link to this URL
   */
  readonly routerLink = input<string>();

  /**
   * Color of the tag to use for the status
   * If provided, the status will be shown as a tag with this color
   * If not provided, the status will be shown as plain text
   */
  readonly tagColor = input<GovUKTagColor>();

  /**
   * When true, the task item will not be rendered as a link
   * This is useful for tasks that are not yet available or accessible
   * @default false
   */
  readonly noLink = input(false, { transform: booleanAttribute });
}

import { Directive } from '@angular/core';
import { ControlValueAccessor, NG_VALUE_ACCESSOR } from '@angular/forms';

/**
 * A standalone directive that implements ControlValueAccessor.
 * This directive is used to create custom form controls that work seamlessly with Angular's forms.
 *
 * It provides itself as a NG_VALUE_ACCESSOR, allowing it to be used in form controls.
 * The multi: true option allows multiple value accessors to be registered for a single form control.
 */
@Directive({
  providers: [
    {
      provide: NG_VALUE_ACCESSOR,
      multi: true,
      useExisting: ValueAccessorDirective,
    },
  ],
})
export class ValueAccessorDirective implements ControlValueAccessor {
  writeValue(value: unknown): void {}
  registerOnChange(fn: (value: unknown) => void): void {}
  registerOnTouched(fn: () => void): void {}
}

