import { CdkAccordion } from '@angular/cdk/accordion';
import { NgTemplateOutlet } from '@angular/common';
import {
  ChangeDetectionStrategy,
  Component,
  computed,
  contentChildren,
  effect,
  input,
  model,
} from '@angular/core';
import { GovUKAccordionItemComponent } from '../accordion-item/accordion-item.component';

@Component({
  imports: [NgTemplateOutlet],
  selector: 'ngx-govuk-accordion',
  templateUrl: './accordion.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
  hostDirectives: [CdkAccordion],
})
export class GovUKAccordionComponent {
  readonly id = input.required<string>();
  readonly initialExpanded = input<string[]>([]);

  readonly expandedItems = model<string[]>([]);

  readonly items = contentChildren(GovUKAccordionItemComponent);

  readonly allExpanded = computed(() => {
    const allItems = this.items();
    const expandedItems = this.expandedItems();
    return (
      allItems.length > 0 &&
      allItems.every((item) => expandedItems.includes(item.itemId()))
    );
  });

  constructor() {
    effect(() => {
      const initialExpandedItems = this.expandedItems();
      const initialExpanded = this.initialExpanded();

      if (initialExpandedItems.length === 0 && initialExpanded.length > 0) {
        this.expandedItems.set([...initialExpanded]);
      }
    });
  }

  isExpanded(itemId: string): boolean {
    return this.expandedItems().includes(itemId);
  }

  toggleItem(itemId: string): void {
    const currentExpanded = this.expandedItems();
    const index = currentExpanded.indexOf(itemId);

    if (index === -1) {
      this.expandedItems.set([...currentExpanded, itemId]);
    } else {
      const newExpanded = [...currentExpanded];
      newExpanded.splice(index, 1);
      this.expandedItems.set(newExpanded);
    }
  }

  toggleAll(): void {
    if (this.allExpanded()) {
      this.expandedItems.set([]);
    } else {
      this.expandedItems.set(this.items().map((item) => item.itemId()));
    }
  }
}

import {
  ChangeDetectionStrategy,
  Component,
  input,
  TemplateRef,
  viewChild,
} from '@angular/core';

@Component({
  selector: 'ngx-govuk-accordion-item',
  templateUrl: './accordion-item.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class GovUKAccordionItemComponent {
  readonly itemId = input.required<string>();
  readonly heading = input.required<string>();
  readonly summary = input<string>();
  readonly expanded = input(false);

  readonly content = viewChild.required<TemplateRef<unknown>>('content');
}

import {
  booleanAttribute,
  ChangeDetectionStrategy,
  Component,
  input,
} from '@angular/core';
import { RouterLink } from '@angular/router';

/**
 * A component that renders a GOV.UK back link.
 *
 * This component implements the GOV.UK Design System back link component.
 * It provides a consistent way to help users navigate back to the previous page.
 *
 * @example
 * ```html
 * <ngx-govuk-back-link></ngx-govuk-back-link>
 * ```
 *
 * @example With inverse color scheme
 * ```html
 * <ngx-govuk-back-link [inverse]="true"></ngx-govuk-back-link>
 * ```
 *
 * @property {boolean} inverse - When true, displays the back link in inverse colors
 *                              for use on darker backgrounds. Defaults to false.
 *
 * @see {@link https://design-system.service.gov.uk/components/back-link/ GOV.UK Back link}
 */
@Component({
    selector: 'ngx-govuk-back-link',
    imports: [RouterLink],
    templateUrl: './back-link.component.html',
    changeDetection: ChangeDetectionStrategy.OnPush
})
export class GovUKBackLinkComponent {
  readonly link = input.required<RouterLink['routerLink']>();

  readonly inverse = input(false, { transform: booleanAttribute });
}

import {
  ChangeDetectionStrategy,
  Component,
  contentChildren,
} from '@angular/core';
import { RouterLink } from '@angular/router';
import { GovUKBreadcrumbDirective } from './breadcrumb/breadcrumb.directive';

@Component({
    selector: 'ngx-govuk-breadcrumbs',
    imports: [RouterLink],
    templateUrl: './breadcrumbs.component.html',
    changeDetection: ChangeDetectionStrategy.OnPush
})
export class GovUKBreadcrumbsComponent {
  readonly items = contentChildren(GovUKBreadcrumbDirective);
}

import { NgClass } from '@angular/common';
import { ChangeDetectionStrategy, Component, input } from '@angular/core';

/**
 * The color type for GovUK buttons
 * @typedef {('primary'|'secondary'|'warning'|'inverse')} GovUKButtonColorType
 * - 'primary' - Default blue button
 * - 'secondary' - Grey button for secondary actions
 * - 'warning' - Red warning button
 * - 'inverse' - White button on dark background
 */
export type GovUKButtonColorType =
  | 'primary'
  | 'secondary'
  | 'warning'
  | 'inverse';

/**
 * A button component following the GovUK Design System
 * @component
 *
 * @example
 * <ngx-govuk-button color="primary">Save and continue</ngx-govuk-button>
 *
