import { NgClass } from '@angular/common';
import {
  ChangeDetectionStrategy,
  Component,
  computed,
  inject,
  input,
} from '@angular/core';
import {
  AbstractControl,
  FormControl,
  FormGroup,
  ReactiveFormsModule,
  ValidatorFn,
  Validators,
} from '@angular/forms';
import {
  GovUKCommonFormInputDirective,
  ValueAccessorDirective,
  injectNgControl,
  inputCommonInputs,
} from 'ngx-govuk-frontend/form-utils';

interface DateInputParts {
  day?: string;
  month?: string;
  year?: string;
}

/**
 * A form component for date input that follows the GOV.UK Design System styling and behavior.
 *
 * This component provides a set of input fields for day, month, and year with proper validation and styling.
 * It integrates with Angular's form controls and supports accessibility features.
 *
 * @example
 * ```html
 * <ngx-govuk-date-input
 *   formControlName="dateOfBirth"
 *   inputId="date-of-birth"
 *   min="1900-01-01"
 *   max="2050-12-31"
 * ></ngx-govuk-date-input>
 * ```
 *
 * @property {string} inputId - Optional. The ID prefix for the input elements.
 * @property {string} extraClasses - Optional. Additional CSS classes to apply to the input elements.
 * @property {string} min - Optional. The minimum valid date in ISO format (YYYY-MM-DD).
 * @property {string} max - Optional. The maximum valid date in ISO format (YYYY-MM-DD).
 */
@Component({
    selector: 'ngx-govuk-date-input',
    imports: [NgClass, ReactiveFormsModule],
    templateUrl: './date-input.component.html',
    changeDetection: ChangeDetectionStrategy.OnPush,
    hostDirectives: [
        ValueAccessorDirective,
        { directive: GovUKCommonFormInputDirective, inputs: inputCommonInputs },
    ]
})
export class GovUKDateInputComponent {
  readonly ngControl = injectNgControl();
  readonly commonFormInput = inject(GovUKCommonFormInputDirective);

  /**
   * The minimum valid date in ISO format (YYYY-MM-DD).
   * Default is 1900-01-01.
   */
  readonly min = input('1900-01-01');

  /**
   * The maximum valid date in ISO format (YYYY-MM-DD).
   * Default is 100 years from now.
   */
  readonly max = input(
    new Date(new Date().getFullYear() + 100, 11, 31)
      .toISOString()
      .split('T')[0],
  );

  private readonly minDate = computed(() => {
    try {
      return new Date(this.min());
    } catch (e) {
      return new Date(1900, 0, 1); // January 1, 1900
    }
  });

  private readonly maxDate = computed(() => {
    try {
      return new Date(this.max());
    } catch (e) {
      // Default to 100 years from now
      return new Date(new Date().getFullYear() + 100, 11, 31); // December 31, current year + 100
    }
  });

  protected dateForm = new FormGroup(
    {
      day: new FormControl<string>('', [
        Validators.pattern(/^([1-9]|0[1-9]|[12]\d|3[01])$/),
      ]),
      month: new FormControl<string>('', [
        Validators.pattern(/^([1-9]|0[1-9]|1[012])$/),
      ]),
      year: new FormControl<string>('', [Validators.pattern(/^\d{4}$/)]),
    },
    { validators: this.dateValidator() },
  );

  constructor() {
    this.dateForm.valueChanges.subscribe((value) => {
      const day = value.day ?? '';
      const month = value.month ?? '';
      const year = value.year ?? '';
      this.onDateFormChange({ day, month, year });

      // Collect all errors from the form and individual controls
      const errors: Record<string, unknown> = {};

      // Add form-level errors (like invalidDate)
      if (this.dateForm.errors) {
        Object.assign(errors, this.dateForm.errors);
      }

      // Add field-level errors with prefixes
      const dayControl = this.dateForm.get('day');
      const monthControl = this.dateForm.get('month');
      const yearControl = this.dateForm.get('year');

      if (dayControl?.errors) {
        Object.entries(dayControl.errors).forEach(([key, value]) => {
          errors[`day_${key}`] = value;
        });
      }

      if (monthControl?.errors) {
        Object.entries(monthControl.errors).forEach(([key, value]) => {
          errors[`month_${key}`] = value;
        });
      }

      if (yearControl?.errors) {
        Object.entries(yearControl.errors).forEach(([key, value]) => {
          errors[`year_${key}`] = value;
        });
      }

      // Set errors on the parent control if there are any
      if (Object.keys(errors).length > 0) {
        this.ngControl.control?.setErrors(errors);
      } else {
        // Clear errors if all fields are valid
        this.ngControl.control?.setErrors(null);
      }
    });

    // Handle changes from the parent form control
    this.ngControl.valueChanges?.subscribe((value) => {
      this.updateDateForm(value);
    });
  }

  setDisabledState(isDisabled: boolean): void {
    if (isDisabled) {
      this.dateForm.disable();
    } else {
      this.dateForm.enable();
    }
  }

  // Expose date parts for testing
  getDateParts(): DateInputParts {
    return {
      day: this.dateForm.get('day')?.value || '',
      month: this.dateForm.get('month')?.value || '',
      year: this.dateForm.get('year')?.value || '',
    };
  }

  // Custom validator to check if a date is valid
  private dateValidator(): ValidatorFn {
    return (control: AbstractControl) => {
      const formGroup = control as FormGroup;
      const day = formGroup.get('day')?.value;
      const month = formGroup.get('month')?.value;
      const year = formGroup.get('year')?.value;

      // If any field is empty, don't perform validation yet
      if (!day || !month || !year) {
        return null;
      }

      // Convert string values to numbers
      const dayNum = parseInt(day, 10);
      const monthNum = parseInt(month, 10);
      const yearNum = parseInt(year, 10);

      // Create a date object - note months are 0-indexed in JS
      const date = new Date(yearNum, monthNum - 1, dayNum);

      // Check if the date is valid
      // For example, 31/04/2020 (April 31st) is not valid
      if (
        date.getFullYear() !== yearNum ||
        date.getMonth() !== monthNum - 1 ||
        date.getDate() !== dayNum
      ) {
        return { invalidDate: true };
      }

      // Check min date
      if (this.minDate() && date < this.minDate()) {
        return { dateTooEarly: true, minDate: this.min() };
      }

      // Check max date
      if (this.maxDate() && date > this.maxDate()) {
        return { dateTooLate: true, maxDate: this.max() };
      }

      return null;
    };
  }

  private onDateFormChange(value: {
    day: string;
    month: string;
    year: string;
  }): void {
    const { day, month, year } = value;

    if (!day && !month && !year) {
      this.ngControl.control?.setValue(null);
      return;
    }

    const paddedDay = day.padStart(2, '0');
    const paddedMonth = month.padStart(2, '0');

    if (day && month && year) {
      // Full date is available, use ISO format
      this.ngControl.control?.setValue(`${year}-${paddedMonth}-${paddedDay}`);
    } else {
      // Partial date, pass as object
      this.ngControl.control?.setValue({
        day: paddedDay,
        month: paddedMonth,
        year,
      });
    }

    this.ngControl.control.markAsTouched();
  }

  // Update internal date form based on parent value
  private updateDateForm(value: string | DateInputParts | null): void {
    if (!value) {
      this.dateForm.reset(
        { day: '', month: '', year: '' },
        { emitEvent: false },
      );
      return;
    }

    let day = '';
    let month = '';
    let year = '';

    if (typeof value === 'string') {
      // Handle ISO date format (YYYY-MM-DD)
      const dateParts = value.split('-');
      if (dateParts.length === 3) {
        year = dateParts[0];
        month = dateParts[1].replace(/^0/, ''); // Remove leading zero
        day = dateParts[2].replace(/^0/, ''); // Remove leading zero
      }
    } else {
      // Handle object format { day, month, year }
      day = value.day?.replace(/^0/, '') || '';
      month = value.month?.replace(/^0/, '') || '';
      year = value.year || '';
    }

    this.dateForm.setValue({ day, month, year }, { emitEvent: false });
  }

  protected getInputId(part: string): string | null {
    return this.commonFormInput.inputId()
      ? `${this.commonFormInput.inputId()}-${part}`
      : null;
  }

  protected hasFieldError(field: string): boolean {
    const control = this.dateForm.get(field);
    return !!control && control.touched && !!control.errors;
  }
}

import {
  ChangeDetectionStrategy,
  Component,
  inject,
  input,
} from '@angular/core';
import { ReactiveFormsModule } from '@angular/forms';
import {
  GovUKCommonFormInputDirective,
  ValueAccessorDirective,
  injectNgControl,
  inputCommonInputs,
} from 'ngx-govuk-frontend/form-utils';

/**
 * Interface defining an option in the select dropdown.
 */
export interface GovUKSelectOption {
  /** The displayed text for the option */
  label: string;
  /** The value that will be submitted when the option is selected */
  value: string;
}

/**
 * This component implements the GOV.UK Design System select component.
 * It allows users to choose an option from a dropdown list.
 *
 * @example
 * ```html
 * <ngx-govuk-select
 *   [options]="[
 *     { value: 'england', label: 'England' },
 *     { value: 'scotland', label: 'Scotland' },
 *     { value: 'wales', label: 'Wales' },
 *     { value: 'northern-ireland', label: 'Northern Ireland' }
 *   ]"
 *   formControlName="country"
 *   inputId="country"
 *   label="Country"
 * ></ngx-govuk-select>
 * ```
 *
 * @see {@link https://design-system.service.gov.uk/components/select/ GOV.UK Select}
 */
@Component({
  selector: 'ngx-govuk-select',
  imports: [ReactiveFormsModule],
  templateUrl: './select.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
  hostDirectives: [
    ValueAccessorDirective,
    { directive: GovUKCommonFormInputDirective, inputs: inputCommonInputs },
  ],
})
export class GovUKSelectComponent {
  /** The Angular form control associated with this select input. */
  readonly ngControl = injectNgControl();

  /** Reference to the common form input directive that provides shared functionality. */
  readonly commonFormInput = inject(GovUKCommonFormInputDirective);

  /** The list of options to display in the select dropdown. This input is required. */
  readonly options = input.required<GovUKSelectOption[]>();
}

import {
  ChangeDetectionStrategy,
  Component,
  computed,
  input,
  output,
} from '@angular/core';

/**
 * Internal interface for pagination items
 */
interface PageItem {
  /** The number/text to display for the item */
  number: string;
  /** Whether this is the current active page */
  current?: boolean;
  /** Whether to display this item as an ellipsis */
  ellipsis?: boolean;
  /** Actual page number for this item */
  pageNumber?: number;
  /** Unique identifier for tracking in loops */
  id: string;
}

/**
 * Pagination mode - defines layout and behavior
 */
export type PaginationMode = 'block' | 'list';

/**
 * A pagination component following the GovUK Design System
 * @component
 *
 * @example
 * <ngx-govuk-pagination
 *   [itemCount]="100"
 *   [itemsPerPage]="10"
 *   [currentPage]="1"
 *   (pageChange)="onPageChange($event)"
 * ></ngx-govuk-pagination>
 */
@Component({
  selector: 'ngx-govuk-pagination',
  templateUrl: './pagination.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class GovUKPaginationComponent {
  /** Total number of items */
  readonly itemCount = input<number>(0);

  /** Number of items per page */
  readonly itemsPerPage = input<number>(10);

  /** Current page number (1-based) */
  readonly currentPage = input<number>(1);

  /** Pagination mode - 'block' or 'list' */
  readonly mode = input<PaginationMode>('list');

  /** Title for the previous button */
  readonly previousTitle = input<string>('Previous');

  /** Label for the previous button (additional context) */
  readonly previousLabel = input<string | null>(null);

  /** Title for the next button */
  readonly nextTitle = input<string>('Next');

  /** Label for the next button (additional context) */
  readonly nextLabel = input<string | null>(null);

  /** Output event when a pagination item is clicked */
  readonly pageChange = output<number>();

  /** Output event when the previous link is clicked */
  readonly previousClick = output<void>();

  /** Output event when the next link is clicked */
  readonly nextClick = output<void>();

  /** Counter for generating unique IDs */
  private ellipsisCounter = 0;

  /** Computed total number of pages */
  readonly totalPages = computed(() =>
    Math.ceil(this.itemCount() / this.itemsPerPage()),
  );

  /** Computed flag for whether previous button should be shown */
  readonly hasPreviousPage = computed(() => this.currentPage() > 1);

  /** Computed flag for whether next button should be shown */
  readonly hasNextPage = computed(() => this.currentPage() < this.totalPages());

  /** Computed check if mode is block */
  readonly isBlockMode = computed(() => this.mode() === 'block');

  /** Computed pagination items */
  readonly computedItems = computed(() => {
    const totalPages = this.totalPages();
    if (totalPages <= 0) {
      return [];
    }

    // Simple case: 7 or fewer pages, show all
    if (totalPages <= 7) {
      return this.createSequentialItems(1, totalPages);
    }

    const currentPage = this.currentPage();
    const items: PageItem[] = [];

    // Always show first page
    items.push(this.createPageItem(1));

    // Complex case: more than 7 pages, show ellipses
    if (currentPage <= 4) {
      // Near the start: show 1 2 3 4 5 ... 100
      items.push(...this.createSequentialItems(2, 5));
      items.push(this.createEllipsisItem());
      items.push(this.createPageItem(totalPages));
    } else if (currentPage >= totalPages - 3) {
      // Near the end: show 1 ... 96 97 98 99 100
      items.push(this.createEllipsisItem());
      items.push(...this.createSequentialItems(totalPages - 4, totalPages));
    } else {
      // In the middle: show 1 ... 49 50 51 ... 100
      items.push(this.createEllipsisItem());
      items.push(
        ...this.createSequentialItems(currentPage - 1, currentPage + 1),
      );
      items.push(this.createEllipsisItem());
      items.push(this.createPageItem(totalPages));
    }

    return items;
  });

  /**
   * Handle page number click
   */
  onPageClick(item: PageItem): void {
    if (!item.ellipsis && item.pageNumber) {
      this.pageChange.emit(item.pageNumber);
    }
  }

  /**
   * Creates a page item for the specified page number
   */
  private createPageItem(pageNumber: number): PageItem {
    return {
      number: String(pageNumber),
      current: pageNumber === this.currentPage(),
      pageNumber: pageNumber,
      id: `page-${pageNumber}`,
    };
  }

  /**
   * Creates an ellipsis item
   */
  private createEllipsisItem(): PageItem {
    return {
      number: '',
      ellipsis: true,
      id: `ellipsis-${++this.ellipsisCounter}`,
    };
  }

  /**
   * Creates an array of sequential page items
   */
  private createSequentialItems(start: number, end: number): PageItem[] {
    const items: PageItem[] = [];
    for (let i = start; i <= end; i++) {
      items.push(this.createPageItem(i));
    }
    return items;
  }
}

