import { CdkAccordion } from '@angular/cdk/accordion';
import { NgTemplateOutlet } from '@angular/common';
import {
  ChangeDetectionStrategy,
  Component,
  computed,
  contentChildren,
  effect,
  input,
  model,
} from '@angular/core';
import { GovUKAccordionItemComponent } from '../accordion-item/accordion-item.component';

/**
 * This component implements the GOV.UK Design System accordion component.
 * It allows users to show and hide sections of related content on a page.
 *
 * @example
 * ```
 * <ngx-govuk-accordion id="accordion-default">
 *   <ngx-govuk-accordion-item heading="Section 1" id="section-1">
 *     <p>This is the content for section 1.</p>
 *   </ngx-govuk-accordion-item>
 *   <ngx-govuk-accordion-item heading="Section 2" id="section-2">
 *     <p>This is the content for section 2.</p>
 *   </ngx-govuk-accordion-item>
 * </ngx-govuk-accordion>
 * ```
 *
 * @see {@link https://design-system.service.gov.uk/components/accordion/ GOV.UK Accordion}
 */
@Component({
  imports: [NgTemplateOutlet],
  selector: 'ngx-govuk-accordion',
  templateUrl: './accordion.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
  hostDirectives: [CdkAccordion],
})
export class GovUKAccordionComponent {
  /** The unique identifier for the accordion. This input is required. */
  readonly id = input.required<string>();

  /**
   * IDs of accordion items that should be expanded when the accordion first renders.
   * Defaults to an empty array (all items collapsed).
   */
  readonly initialExpanded = input<string[]>([]);

  /**
   * Two-way binding model for the currently expanded accordion items.
   * Contains an array of item IDs that are expanded.
   */
  readonly expandedItems = model<string[]>([]);

  /** Collection of accordion item components that are children of this accordion. */
  readonly items = contentChildren(GovUKAccordionItemComponent);

  /**
   * Computed property that determines if all accordion items are currently expanded.
   * Returns true only if there are items and all of them are expanded.
   */
  readonly allExpanded = computed(() => {
    const allItems = this.items();
    const expandedItems = this.expandedItems();
    return (
      allItems.length > 0 &&
      allItems.every((item) => expandedItems.includes(item.itemId()))
    );
  });

  constructor() {
    effect(() => {
      const initialExpandedItems = this.expandedItems();
      const initialExpanded = this.initialExpanded();

      if (initialExpandedItems.length === 0 && initialExpanded.length > 0) {
        this.expandedItems.set([...initialExpanded]);
      }
    });
  }

  /**
   * Checks if a specific accordion item is expanded.
   * @param itemId The ID of the accordion item to check
   * @returns True if the item is expanded, false otherwise
   */
  isExpanded(itemId: string): boolean {
    return this.expandedItems().includes(itemId);
  }

  /**
   * Toggles the expanded state of a specific accordion item.
   * If the item is collapsed, it will be expanded, and vice versa.
   * @param itemId The ID of the accordion item to toggle
   */
  toggleItem(itemId: string): void {
    const currentExpanded = this.expandedItems();
    const index = currentExpanded.indexOf(itemId);

    if (index === -1) {
      this.expandedItems.set([...currentExpanded, itemId]);
    } else {
      const newExpanded = [...currentExpanded];
      newExpanded.splice(index, 1);
      this.expandedItems.set(newExpanded);
    }
  }

  /**
   * Toggles all accordion items between expanded and collapsed states.
   * If all items are currently expanded, collapses all items.
   * If any items are collapsed, expands all items.
   */
  toggleAll(): void {
    if (this.allExpanded()) {
      this.expandedItems.set([]);
    } else {
      this.expandedItems.set(this.items().map((item) => item.itemId()));
    }
  }
}

import {
  booleanAttribute,
  ChangeDetectionStrategy,
  Component,
  input,
  TemplateRef,
  viewChild,
} from '@angular/core';

/**
 * This component implements a section in the GOV.UK Design System accordion component.
 * It contains the heading, optional summary, and content for an accordion section.
 *
 * @example
 * ```html
 * <ngx-govuk-accordion-item
 *   itemId="section-1"
 *   heading="Section 1"
 *   summary="This section contains information about..."
 *   [expanded]="true">
 *   <p>Content for section 1</p>
 * </ngx-govuk-accordion-item>
 * ```
 *
 * @see {@link https://design-system.service.gov.uk/components/accordion/ GOV.UK Accordion}
 */
@Component({
  selector: 'ngx-govuk-accordion-item',
  templateUrl: './accordion-item.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class GovUKAccordionItemComponent {
  /** The unique identifier for the accordion item. This input is required. */
  readonly itemId = input.required<string>();

  /** The text displayed in the accordion item heading. This input is required. */
  readonly heading = input.required<string>();

  /** Optional summary text displayed below the heading. */
  readonly summary = input<string>();

  /** Whether the accordion item is expanded. Defaults to false. */
  readonly expanded = input(false, { transform: booleanAttribute });

  /** Reference to the template containing the accordion item's content. */
  readonly content = viewChild.required<TemplateRef<unknown>>('content');
}

import {
  booleanAttribute,
  ChangeDetectionStrategy,
  Component,
  input,
} from '@angular/core';
import { RouterLink } from '@angular/router';

/**
 * This component implements the GOV.UK Design System back link component.
 * It provides a consistent way to help users navigate back to the previous page.
 *
 * @example
 * ```html
 * <ngx-govuk-back-link></ngx-govuk-back-link>
 * ```
 *
 * @example With inverse color scheme
 * ```html
 * <ngx-govuk-back-link [inverse]="true"></ngx-govuk-back-link>
 * ```
 *
 * @see {@link https://design-system.service.gov.uk/components/back-link/ GOV.UK Back link}
 */
@Component({
  selector: 'ngx-govuk-back-link',
  imports: [RouterLink],
  templateUrl: './back-link.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class GovUKBackLinkComponent {
  readonly link = input.required<RouterLink['routerLink']>();

  readonly inverse = input(false, { transform: booleanAttribute });
}

import {
  ChangeDetectionStrategy,
  Component,
  contentChildren,
} from '@angular/core';
import { RouterLink } from '@angular/router';
import { GovUKBreadcrumbDirective } from './breadcrumb/breadcrumb.directive';

/**
 * This component implements the GOV.UK Design System breadcrumbs component.
 * It helps users understand where they are in a website's structure and navigate back to previous pages.
 *
 * @example
 * ```html
 * <ngx-govuk-breadcrumbs>
 *   <a govukBreadcrumb routerLink="/">Home</a>
 *   <a govukBreadcrumb routerLink="/benefits">Benefits</a>
 *   <a govukBreadcrumb>Eligibility</a>
 * </ngx-govuk-breadcrumbs>
 * ```
 *
 * @see {@link https://design-system.service.gov.uk/components/breadcrumbs/ GOV.UK Breadcrumbs}
 */
@Component({
  selector: 'ngx-govuk-breadcrumbs',
  imports: [RouterLink],
  templateUrl: './breadcrumbs.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class GovUKBreadcrumbsComponent {
  /** Collection of breadcrumb items that are children of this component. */
  readonly items = contentChildren(GovUKBreadcrumbDirective);
}

import { NgClass } from '@angular/common';
import {
  booleanAttribute,
  ChangeDetectionStrategy,
  Component,
  input,
} from '@angular/core';

/**
 * The color type for GovUK buttons
 * @typedef {('primary'|'secondary'|'warning'|'inverse')} GovUKButtonColorType
 * - 'primary' - Default blue button
 * - 'secondary' - Grey button for secondary actions
 * - 'warning' - Red warning button
 * - 'inverse' - White button on dark background
 */
export type GovUKButtonColorType =
  | 'primary'
  | 'secondary'
  | 'warning'
  | 'inverse';

/**
 * This component implements the GOV.UK Design System button component.
 * It allows users to take actions, submit forms, or navigate to a new page.
 *
 * @example
 * ```html
 * <ngx-govuk-button>Save and continue</ngx-govuk-button>
 * ```
 *
 * @example With secondary style
 * ```html
 * <ngx-govuk-button color="secondary">Cancel</ngx-govuk-button>
 * ```
 *
 * @example With start icon
 * ```html
 * <ngx-govuk-button [start]="true">Start now</ngx-govuk-button>
 * ```
 *
 * @see {@link https://design-system.service.gov.uk/components/button/ GOV.UK Button}
 */
@Component({
  selector: 'ngx-govuk-button',
  imports: [NgClass],
  templateUrl: './button.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class GovUKButtonComponent {
  /** Whether the button is disabled. Defaults to false. */
  readonly disabled = input(false, { transform: booleanAttribute });

