 */
@Component({
  selector: 'ngx-govuk-tab',
  templateUrl: './tab.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class GovUKTabComponent {
  /** The unique identifier for the tab. This input is required. */
  readonly id = input.required<string>();

  /** The text displayed in the tab. This input is required. */
  readonly title = input.required<string>();

  /** Reference to the template containing the tab's content. */
  readonly tabContent = viewChild.required<TemplateRef<unknown>>('tabContent');
}

import { CdkTableModule } from '@angular/cdk/table';
import {
  booleanAttribute,
  ChangeDetectionStrategy,
  Component,
  computed,
  input,
} from '@angular/core';

export type GovUKTableColumn = {
  header: string;
  key: string;
  isRowHeader?: boolean;
  isNumeric?: boolean;
  customClass?: string;
};

/**
 * This component implements the GOV.UK Design System table component.
 * It provides a way to make information easier to compare and scan for users.
 *
 * @example
 * ```html
 * <ngx-govuk-table
 *   [columns]="[
 *     { header: 'Month', key: 'month', isRowHeader: true },
 *     { header: 'Rate', key: 'rate', isNumeric: true }
 *   ]"
 *   [dataSource]="[
 *     { month: 'January', rate: '3.50' },
 *     { month: 'February', rate: '5.00' }
 *   ]"
 *   caption="Payment rates">
 * </ngx-govuk-table>
 * ```
 *
 * @see {@link https://design-system.service.gov.uk/components/table/ GOV.UK Table}
 */
@Component({
  selector: 'ngx-govuk-table',
  imports: [CdkTableModule],
  templateUrl: './table.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class GovUKTableComponent {
  /** The columns configuration for the table. This input is required. */
  readonly columns = input.required<GovUKTableColumn[]>();

  /** The data to display in the table. This input is required. */
  readonly dataSource = input.required<unknown[]>();

  /** Optional caption for the table to describe its contents. */
  readonly caption = input<string>();

  /** Whether to use a smaller font size for the table. Defaults to false. */
  readonly small = input(false, { transform: booleanAttribute });

  /**
   * Computed array of column keys used to access data from the data source.
   * Created from the keys in the columns array.
   */
  readonly columnKeys = computed(() =>
    this.columns().map((column) => column.key),
  );
}

import { NgTemplateOutlet } from '@angular/common';
import {
  ChangeDetectionStrategy,
  Component,
  contentChildren,
  input,
  model,
} from '@angular/core';
import { GovUKTabComponent } from '../tab/tab.component';

/**
 * This component implements the GOV.UK Design System tabs component.
 * It lets users navigate between related sections of content on the same page.
 *
 * @example
 * ```html
 * <ngx-govuk-tabs title="Contents">
 *   <ngx-govuk-tab heading="Past day">
 *     <p>Content for the past day tab panel</p>
 *   </ngx-govuk-tab>
 *   <ngx-govuk-tab heading="Past week">
 *     <p>Content for the past week tab panel</p>
 *   </ngx-govuk-tab>
 * </ngx-govuk-tabs>
 * ```
 *
 * @see {@link https://design-system.service.gov.uk/components/tabs/ GOV.UK Tabs}
 */
@Component({
  selector: 'ngx-govuk-tabs',
  imports: [NgTemplateOutlet],
  templateUrl: './tabs.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class GovUKTabsComponent {
  /** The title for the tabs component. This input is required. */
  readonly title = input.required<string>();

  /** Collection of tab components that are children of this tabs component. */
  readonly tabs = contentChildren(GovUKTabComponent);

  /** Two-way binding model for the currently active tab index. Defaults to 0 (first tab). */
  readonly activeTab = model(0);

  /**
   * Selects a tab at the specified index.
   * @param index The index of the tab to select
   * @param event The event that triggered the selection
   */
  selectTab(index: number, event: Event): void {
    event.preventDefault();
    this.activeTab.set(index);
  }
}

