import { NgClass } from '@angular/common';
import {
  booleanAttribute,
  ChangeDetectionStrategy,
  Component,
  computed,
  DestroyRef,
  inject,
  input,
  OnInit,
  signal,
  WritableSignal,
} from '@angular/core';
import { takeUntilDestroyed } from '@angular/core/rxjs-interop';
import { ReactiveFormsModule } from '@angular/forms';
import {
  GovUKCommonFormInputDirective,
  injectNgControl,
  inputCommonInputs,
  ValueAccessorDirective,
} from 'ngx-govuk-frontend/form-utils';

/**
 * @example
 * ```html
 * <ngx-govuk-textarea
 *   formControlName="detail"
 *   inputId="detail"
 *   [rows]="5"
 * ></ngx-govuk-textarea>
 * ```
 *
 * @example
 * ```html
 * <ngx-govuk-textarea
 *   formControlName="detail"
 *   inputId="detail"
 *   [rows]="5"
 *   [maxLength]="200"
 *   [showCharacterCount]="true"
 * ></ngx-govuk-textarea>
 * ```
 *
 * @property {string} autocomplete - HTML autocomplete attribute value
 * @property {string} extraClasses - Additional CSS classes to apply to the textarea
 * @property {string} inputId - Required. The ID attribute for the input element.
 * @property {number} rows - Number of visible text rows for the textarea. Defaults to 2.
 * @property {number} maxLength - Optional. Maximum number of characters allowed in the textarea.
 * @property {boolean} showCharacterCount - Optional. Whether to show the character count. Defaults to false.
 */
@Component({
  selector: 'ngx-govuk-textarea',
  imports: [NgClass, ReactiveFormsModule],
  templateUrl: './textarea.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
  hostDirectives: [
    ValueAccessorDirective,
    { directive: GovUKCommonFormInputDirective, inputs: inputCommonInputs },
  ],
})
export class GovUKTextareaComponent implements OnInit {
  readonly ngControl = injectNgControl();
  readonly commonFormInput = inject(GovUKCommonFormInputDirective);

  private readonly destroyRef = inject(DestroyRef);

  readonly rows = input(2);
  readonly maxLength = input<number | null>(null);
  readonly showCharacterCount = input(false, { transform: booleanAttribute });

  private readonly currentLength: WritableSignal<number> = signal(0);
  private readonly remainingCharacters = computed(() => {
    const maxLength = this.maxLength();
    return maxLength === null ? null : maxLength - this.currentLength();
  });

  readonly characterCountMessage = computed(() => {
    const remaining = this.remainingCharacters();

    if (this.maxLength() === null || remaining === null) {
      return null;
    }

    if (remaining < 0) {
      const overLimit = Math.abs(remaining);
      return `You have ${overLimit} ${overLimit === 1 ? 'character' : 'characters'} too many`;
    }

    return `You have ${remaining} ${remaining === 1 ? 'character' : 'characters'} remaining`;
  });

  ngOnInit(): void {
    this.ngControl.valueChanges
      ?.pipe(takeUntilDestroyed(this.destroyRef))
      .subscribe((value) => this.currentLength.set(value.length));
  }
}

import { NgClass } from '@angular/common';
import {
  ChangeDetectionStrategy,
  Component,
  inject,
  input,
} from '@angular/core';
import { ReactiveFormsModule } from '@angular/forms';
import {
  GovUKCommonFormInputDirective,
  ValueAccessorDirective,
  injectNgControl,
  inputCommonInputs,
} from 'ngx-govuk-frontend/form-utils';

/**
 * A form input component that follows the GOV.UK Design System styling and behavior.
 *
 * This component provides a text input field with optional features like autocomplete, prefixes, and suffixes.
 * It integrates with Angular's form controls and supports accessibility features.
 *
 * @example
 * ```html
 * <ngx-govuk-text-input
 *   formControlName="firstName"
 *   inputId="first-name"
 *   autocomplete="given-name"
 * ></ngx-govuk-text-input>
 * ```
 *
 * @property {string} autocomplete - Optional. The HTML autocomplete attribute value. Defaults to 'off'.
 * @property {string} extraClasses - Optional. Additional CSS classes to apply to the input element.
 * @property {string} inputId - Optional. The ID attribute for the input element.
 * @property {string} prefix - Optional. Text or symbol displayed before the input field (e.g., '').
 * @property {string} suffix - Optional. Text or symbol displayed after the input field (e.g., 'per day').
 */
@Component({
    selector: 'ngx-govuk-text-input',
    imports: [NgClass, ReactiveFormsModule],
    templateUrl: './text-input.component.html',
    changeDetection: ChangeDetectionStrategy.OnPush,
    hostDirectives: [
        ValueAccessorDirective,
        { directive: GovUKCommonFormInputDirective, inputs: inputCommonInputs },
    ]
})
export class GovUKTextInputComponent {
  readonly ngControl = injectNgControl();
  readonly commonFormInput = inject(GovUKCommonFormInputDirective);

  readonly prefix = input<string>();
  readonly suffix = input<string>();
}

import { ChangeDetectionStrategy, Component } from '@angular/core';

/**
 * This component implements the GOV.UK Design System warning text component.
 * It helps make users aware of important information that could affect their application or journey.
 *
 * @example
 * ```html
 * <ngx-govuk-warning-text>
 *   You can be fined up to 5,000 if you don't register.
 * </ngx-govuk-warning-text>
 * ```
 *
 * @see {@link https://design-system.service.gov.uk/components/warning-text/ GOV.UK Warning text}
 */
@Component({
  selector: 'ngx-govuk-warning-text',
  templateUrl: './warning-text.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class GovUKWarningTextComponent {}

