import { NgClass } from '@angular/common';
import { ChangeDetectionStrategy, Component, input } from '@angular/core';

/**
 * A component that renders a GOV.UK back link.
 *
 * This component implements the GOV.UK Design System back link component.
 * It provides a consistent way to help users navigate back to the previous page.
 *
 * @example
 * ```html
 * <ngx-govuk-back-link></ngx-govuk-back-link>
 * ```
 *
 * @example With inverse color scheme
 * ```html
 * <ngx-govuk-back-link [inverse]="true"></ngx-govuk-back-link>
 * ```
 *
 * @property {boolean} inverse - When true, displays the back link in inverse colors
 *                              for use on darker backgrounds. Defaults to false.
 *
 * @see {@link https://design-system.service.gov.uk/components/back-link/ GOV.UK Back link}
 */
@Component({
  selector: 'ngx-govuk-back-link',
  standalone: true,
  imports: [NgClass],
  templateUrl: './back-link.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class GovUKBackLinkComponent {
  readonly inverse = input(false);
}

import { NgClass } from '@angular/common';
import { ChangeDetectionStrategy, Component, input } from '@angular/core';

/**
 * The color type for GovUK buttons
 * @typedef {('primary'|'secondary'|'warning'|'inverse')} GovUKButtonColorType
 * - 'primary' - Default blue button
 * - 'secondary' - Grey button for secondary actions
 * - 'warning' - Red warning button
 * - 'inverse' - White button on dark background
 */
export type GovUKButtonColorType =
  | 'primary'
  | 'secondary'
  | 'warning'
  | 'inverse';

/**
 * A button component following the GovUK Design System
 * @component
 *
 * @example
 * <ngx-govuk-button color="primary">Save and continue</ngx-govuk-button>
 *
 * @property {boolean} disabled - Whether the button is disabled
 * @property {boolean} start - Whether to show the start icon (forward arrow)
 * @property {GovUKButtonColorType} color - The color variant of the button
 *   - 'primary' (default) - Blue button for primary actions
 *   - 'secondary' - Grey button for secondary actions
 *   - 'warning' - Red button for destructive actions
 *   - 'inverse' - White button for use on dark backgrounds
 */
@Component({
  selector: 'ngx-govuk-button',
  standalone: true,
  imports: [NgClass],
  templateUrl: './button.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class GovUKButtonComponent {
  disabled = input(false);
  start = input(false);
  color = input<GovUKButtonColorType>('primary');
}

